import styled from 'styled-components';

export const SignInAndSignUpContainer = styled.div`
  width: 850px;
  display: flex;
  justify-content: center;
  margin: 30px auto;

  @media screen and (max-width: 800px) {
    display: grid;
    width: 100%;
    margin: 30px auto;
  }
`;
