import React from 'react';

import {
  GroupContainer,
  FormInputContainer,
  FormInputLabel
} from './form-input.styles';

const FormInput = ({ handleChange, label, ...props }) => (
  <GroupContainer>
    <FormInputContainer onChange={handleChange} {...props} />
    {
      label ? (
        <FormInputLabel
          className={`${props.value.lenght ? 'shrink' : ''}`}>
            {label}
        </FormInputLabel>
      ) : null}
  </GroupContainer>
)

export default FormInput;
